UPDATE locales_npc_text SET Text0_0_loc4='去铁炉堡的秘法区并且与那里的夜精灵德鲁伊们谈话。他们能将你送到月光林地参加新年节庆庆祝活动。',Text0_1_loc4='去铁炉堡的秘法区并且与那里的夜精灵德鲁伊们谈话。他们能将你送到月光林地参加新年节庆庆祝活动。',Text1_0_loc4='很高兴见到你 $N',Text1_1_loc4='很高兴见到你 $N' WHERE entry=9715; /* Text0_0: Go to Ironforge\'s Mystic Ward and speak with the night elf druids there. They can transport you to Moonglade to join in the Lunar Festival celebration. */ /* Text0_1: Go to Ironforge\'s Mystic Ward and speak with the night elf druids there. They can transport you to Moonglade to join in the Lunar Festival celebration. */ /* Text1_0: MISSING ENGLISH Text1: */ /* Text1_1: MISSING ENGLISH Text1: */
