UPDATE locales_npc_text SET Text0_0_loc4='求求你，$r。$b$b让我解脱吧……',Text0_1_loc4='求求你，$r。$b$b让我解脱吧……',Text1_0_loc4='别犹豫，$c。$b$b动……动手吧……',Text1_1_loc4='别犹豫，$c。$b$b动……动手吧……' WHERE entry=14035; /* Text0_0: Please, $r.$b$bRelease me... */ /* Text0_1: Please, $r.$b$bRelease me... */ /* Text1_0: Don\'t think, $c.$b$bJust... do it.... */ /* Text1_1: Don\'t think, $c.$b$bJust... do it.... */
