UPDATE locales_npc_text SET Text0_0_loc2='Vous pouvez trouver l\'Ancien Couronne-de-neige dans la chapelle de l\'Espoir de lumière des Maleterres de l\'est.',Text0_1_loc2='Vous pouvez trouver l\'Ancien Couronne-de-neige dans la chapelle de l\'Espoir de lumière des Maleterres de l\'est.' WHERE entry=8153; /* Text0_0: Elder Snowcrown can be found in Light\'s Hope Chapel in the Eastern Plaguelands. */ /* Text0_1: Elder Snowcrown can be found in Light\'s Hope Chapel in the Eastern Plaguelands. */
UPDATE locales_npc_text SET Text0_0_loc2='L\'Ancien Haut-pic veille sur les Hinterlands depuis une colline située en leur centre.',Text0_1_loc2='L\'Ancien Haut-pic veille sur les Hinterlands depuis une colline située en leur centre.' WHERE entry=8154; /* Text0_0: Elder Highpeak watches over the Hinterlands from a hill near its center. */ /* Text0_1: Elder Highpeak watches over the Hinterlands from a hill near its center. */
UPDATE locales_npc_text SET Text0_0_loc2='L\'Ancien Tracevent réside dans la Tour de garde de la couronne des Maleterres de l\'est.',Text0_1_loc2='L\'Ancien Tracevent réside dans la Tour de garde de la couronne des Maleterres de l\'est.' WHERE entry=8155; /* Text0_0: Elder Windrun resides in the Crown Guard Tower in the Eastern Plaguelands. */ /* Text0_1: Elder Windrun resides in the Crown Guard Tower in the Eastern Plaguelands. */
UPDATE locales_npc_text SET Text0_0_loc2='L\'Ancien Murmeloin erre dans les rues de Stratholme.',Text0_1_loc2='L\'Ancien Murmeloin erre dans les rues de Stratholme.' WHERE entry=8156; /* Text0_0: Elder Farwhisper can be found wandering the streets of Stratholme. */ /* Text0_1: Elder Farwhisper can be found wandering the streets of Stratholme. */
UPDATE locales_npc_text SET Text0_0_loc2='L\'Ancien Baguefer vit dans la caverne de Noircharbon de la gorge des Vents brûlants.',Text0_1_loc2='L\'Ancien Baguefer vit dans la caverne de Noircharbon de la gorge des Vents brûlants.' WHERE entry=8157; /* Text0_0: Elder Ironband lives in Blackchar Cave in Searing Gorge. */ /* Text0_1: Elder Ironband lives in Blackchar Cave in Searing Gorge. */
