UPDATE locales_npc_text SET Text0_0_loc2='L\'Ancien Barbe-de-Bronze réside dans le domaine ancestral des Barbe-de-Bronze, Forgefer.',Text0_1_loc2='L\'Ancien Barbe-de-Bronze réside dans le domaine ancestral des Barbe-de-Bronze, Forgefer.' WHERE entry=8162; /* Text0_0: Elder Bronzebeard can be found in the ancestral home of the Bronzebeards, Ironforge. */ /* Text0_1: Elder Bronzebeard can be found in the ancestral home of the Bronzebeards, Ironforge. */
