UPDATE locales_npc_text SET Text0_0_loc2='Qu\'Élune soit avec vous, $c.',Text0_1_loc2='Qu\'Élune soit avec vous, $c.',Text1_0_loc2='La grande prêtresse ne peut pas quitter Darnassus. Fandral est d\'une humeur épouvantable.' WHERE entry=8164; /* Text0_0: Elune be with you, $c. */ /* Text0_1: Elune be with you, $c. */ /* Text1_0: The High Priestess could not leave Darnassus. Fandral is in a very foul mood. */
UPDATE locales_npc_text SET Text2_0_loc2='Tyrande prie pour vous. Mère Lune nous bénit tous.' WHERE entry=8164; /* Text2_0: Tyrande sends her prayers. The Mother Moon blesses us all. */
UPDATE locales_npc_text SET Text0_0_loc2='L\'Ancien Gouffre-du-Matin passe son temps au Cercle de la loi, dans les profondeurs de Rochenoire.',Text0_1_loc2='L\'Ancien Gouffre-du-Matin passe son temps au Cercle de la loi, dans les profondeurs de Rochenoire.' WHERE entry=8166; /* Text0_0: Elder Morndeep spends time at the Ring of Law in Blackrock Depths. */ /* Text0_1: Elder Morndeep spends time at the Ring of Law in Blackrock Depths. */
UPDATE locales_npc_text SET Text0_0_loc2='L\'Ancien Fort-de-pierre s\'est installé dans les oubliettes du pic Rochenoire.',Text0_1_loc2='L\'Ancien Fort-de-pierre s\'est installé dans les oubliettes du pic Rochenoire.' WHERE entry=8167; /* Text0_0: Elder Stonefort has taken to the dungeons of Blackrock Spire. */ /* Text0_1: Elder Stonefort has taken to the dungeons of Blackrock Spire. */
UPDATE locales_npc_text SET Text0_0_loc2='L\'ancien Pierre-Grondante regarde passer les années dans une caverne au sommet du rocher des Cognepeurs, dans les steppes Ardentes.',Text0_1_loc2='L\'ancien Pierre-Grondante regarde passer les années dans une caverne au sommet du rocher des Cognepeurs, dans les steppes Ardentes.' WHERE entry=8168; /* Text0_0: Elder Rumblerock passes the years in a cave atop Dreadmaul Rock in the Burning Steppes. */ /* Text0_1: Elder Rumblerock passes the years in a cave atop Dreadmaul Rock in the Burning Steppes. */
UPDATE locales_npc_text SET Text0_0_loc2='Aux dernières nouvelles, l\'Ancienne Chantétoile se trouvait au Temple englouti, dans le marais des Chagrins.',Text0_1_loc2='Aux dernières nouvelles, l\'Ancienne Chantétoile se trouvait au Temple englouti, dans le marais des Chagrins.' WHERE entry=8169; /* Text0_0: Last I knew, Elder Starsong was in the Sunken Temple of the Swamp of Sorrows. */ /* Text0_1: Last I knew, Elder Starsong was in the Sunken Temple of the Swamp of Sorrows. */
UPDATE locales_npc_text SET Text0_0_loc2='L\'Ancien Front-de-Tempête se trouve probablement au village de Comté-de-l\'Or, dans la forêt d\'Elwynn.',Text0_1_loc2='L\'Ancien Front-de-Tempête se trouve probablement au village de Comté-de-l\'Or, dans la forêt d\'Elwynn.' WHERE entry=8170; /* Text0_0: Elder Stormbrow is most likely at the village of Goldshire in Elwynn Forest. */ /* Text0_1: Elder Stormbrow is most likely at the village of Goldshire in Elwynn Forest. */
UPDATE locales_npc_text SET Text0_0_loc2='Sabot-d\'Hiver ? Il paraît qu\'il s\'est installé à Baie-du-Butin.',Text0_1_loc2='Sabot-d\'Hiver ? Il paraît qu\'il s\'est installé à Baie-du-Butin.' WHERE entry=8171; /* Text0_0: Winterhoof? I hear he\'s living it up in Booty Bay. */ /* Text0_1: Winterhoof? I hear he\'s living it up in Booty Bay. */
