UPDATE locales_npc_text SET Text0_0_loc2='L\'Ancien Lamevive arpente les sentiers de Darnassus, au sommet de Teldrassil.',Text0_1_loc2='L\'Ancien Lamevive arpente les sentiers de Darnassus, au sommet de Teldrassil.' WHERE entry=8176; /* Text0_0: Elder Bladeswift wanders the paths of Darnassus atop Teldrassil. */ /* Text0_1: Elder Bladeswift wanders the paths of Darnassus atop Teldrassil. */
