UPDATE locales_npc_text SET Text0_0_loc2='Aux dernières nouvelles, l\'Ancienne Totem-Runique était en route pour Tranchecolline, en Durotar.',Text0_1_loc2='Aux dernières nouvelles, l\'Ancienne Totem-Runique était en route pour Tranchecolline, en Durotar.' WHERE entry=8191; /* Text0_0: Last I heard, Elder Runetotem was traveling to Razor Hill in Durotar. */ /* Text0_1: Last I heard, Elder Runetotem was traveling to Razor Hill in Durotar. */
