UPDATE script_texts SET content_loc2='Comme si nous n\'avons pas assez de problèmes, vous allez en créer encore plus !' WHERE entry=-1000200; /* content: As if we don\'t have enough problems, you go and create more! */
UPDATE script_texts SET content_loc2='Me voilà sur pieds ! Merci docteur !' WHERE entry=-1000201; /* content: I\'m saved! Thank you, doctor! */
