UPDATE `script_texts` SET `content_loc2` = 'Il ne fallut cependant pas attendre longtemps avant que la ville ne soit à nouveau assaillie. Cette fois, l\'attaque venait des armées d\'Illidan. Un important régiment d\'elfes de sang avait été envoyé par l\'allié d\'Illidan, Kael\'thas Haut-soleil, pour détruire la ville.' WHERE `entry` = -1000242;
