UPDATE locales_page_text SET text_loc3='Holia Sonnenschild$BVerteidigerin des Scharlachroten Kreuzzugs$BStarb, als sie den Schreckenslord Beltheris erschlug' WHERE entry=2174; /* text: Holia Sunshield Defender of the Scarlet Crusade Killed while slaying the Dreadlord Beltheris */
