UPDATE locales_page_text SET text_loc3='<Die Tinte wirbelt und verändert sich überall auf der Seite. Es ist, als ob das Buch Euch verspotten möchte.>' WHERE entry=2634; /* text: <The ink swirls and shifts around the page. You get the feeling that the book is mocking you.> */
