UPDATE locales_npc_text SET Text0_0_loc1='당신은 우리의 동족도 아니고 도전장을 낸 것도 아니니 이곳을 지나갈 수 없습니다.',Text0_1_loc1='당신은 우리의 동족도 아니고 도전장을 낸 것도 아니니 이곳을 지나갈 수 없습니다.' WHERE entry=2600; /* Text0_0: You may not pass.  You are not our kin, nor have you issued the challenge. */ /* Text0_1: You may not pass.  You are not our kin, nor have you issued the challenge. */
