UPDATE locales_npc_text SET Text0_0_loc1='여기서 나가게 해 주게!',Text1_0_loc1='자네는 스컬지가 아니군. 여기서 뭘 하는 건가?' WHERE entry=13390; /* Text0_0: Lemme outta here, mon! */ /* Text1_0: You\'re no Scourge. What you doin\' here, mon? */
