UPDATE locales_page_text SET text_loc8='<HTML>
<BODY>
<H1 align="center">НАСТАВЛЕНИЕ ПО ВОИНСКИМ ЗВАНИЯМ</H1>
<BR/>
<BR/>
<IMG src="InterfacePvPRankBadgesPvPRankAlliance" align="left"/>
<BR/>
<P align="right">Ниже перечислены</P>
<P align="right">воинские звания</P>
<P align="right">Альянса,</P>
<P align="right">снабженные</P>
<P align="right">пояснениями,</P>
<P align="right">касающимися народов</P>
<P align="right">Орды. Звания</P>
<P align="right">представлены в двух</P>
<P align="right">разделах – офицерский и рядовой состав – и перечислены в каждом разделе в убывающем порядке, от старших к младшим. Да здравствует Альянс!</P>
</BODY>
</HTML>' WHERE entry=2654; /* text: <HTML>
<BODY>
<H1 align="center">A TREATISE ON MILITARY RANKS</H1>
<BR/>
<BR/>
<IMG src="Interface\PvPRankBadges\PvPRankAlliance" align="left"/>
<BR/>
<P align="right">What follows are</P>
<P align="right">the military ranks</P>
<P align="right">of the Alliance,</P>
<P align="right">proceeded by such</P>
<P align="right">information as is</P>
<P align="right">known for the races</P>
<P align="right">of the Horde.  Each</P>
<P align="right">is presented in two</P>
<P align="right">sections, the officers and then the enlisted, with the ranks of each listed in descending order from highest to lowest.  Long live the Alliance!</P>
</BODY>
</HTML> */
