UPDATE locales_quest SET RequestItemsText_loc2='You\'ve returned, $N. Is the line of Ignaeus Trollbane broken?' WHERE entry=644; -- RequestItemsText: You\'ve returned, $N. Is the line of Ignaeus Trollbane broken?
