UPDATE locales_quest SET RequestItemsText_loc2='This is the Stone of Inner Binding. It is the strongest of the binding stones and must be unlocked for the others to open.' WHERE entry=651; -- RequestItemsText: This is the Stone of Inner Binding. It is the strongest of the binding stones and must be unlocked for the others to open.
