UPDATE locales_quest SET RequestItemsText_loc2='Did you find those urns, $N? We must find the link between them and the troggs!' WHERE entry=704; -- RequestItemsText: Did you find those urns, $N? We must find the link between them and the troggs!
UPDATE locales_quest SET RequestItemsText_loc2='Did you get my pearls?  I\'m almost out of flash bombs!' WHERE entry=705; -- RequestItemsText: Did you get my pearls?  I\'m almost out of flash bombs!
