UPDATE locales_quest SET offerrewardtext_loc3='News of your deed reached me moments ago -- already the beasts near the well drink from it eagerly.$B$BYou have done a great thing for the land, $N. And you have done a great thing for the Tauren people.' WHERE entry=754;
