UPDATE locales_quest SET offerrewardtext_loc3='Thank you, $N. The purity with which these beasts hunt is vital in the creation of the next cleansing totem.$B$BThe goblins will never learn the difference between contending with the land, and living in harmony with it.' WHERE entry=756;
