UPDATE locales_quest SET offerrewardtext_loc3='Well, I\'ll be...$B$BThis was one of Chen Stormstout\'s kegs. He traveled with Rexxar a long time ago. I haven\'t seen or heard about him in a kodo\'s age. I appreciate you bringing this to me, $N.' WHERE entry=819;
