UPDATE locales_npc_text SET Text0_0_loc9='Nope, don\'t think there are any Enchanters here in Elwynn Forest.  There\'s a guy named Lucan who runs a shop up in Stormwind though.  You could check with him and see if he will train you.',Text0_1_loc9='Nope, don\'t think there are any Enchanters here in Elwynn Forest.  There\'s a guy named Lucan who runs a shop up in Stormwind though.  You could check with him and see if he will train you.',Text1_0_loc9='Hmmm... only enchanters I know of around here work out of Stormwind.  Why don\'t you ask one of the guards up in Stormwind?',Text1_1_loc9='Hmmm... only enchanters I know of around here work out of Stormwind.  Why don\'t you ask one of the guards up in Stormwind?' WHERE entry=4277;
