UPDATE locales_npc_text SET Text0_0_loc9='Lee Brown spends the whole day fishing up by Crystal Lake.  He\'s probably got some stories to tell and some tips he could teach you.',Text0_1_loc9='MISSING ENGLISH Text0:',Text1_0_loc9='Lee\'s caught some pretty odd things up at Crystal Lake.  He told me a story about this weird mask he fished up some months ago.  If you ask me, I think he just had one too many at the Lion\'s Pride that night.',Text1_1_loc9='MISSING ENGLISH Text1:' WHERE entry=4280;
