UPDATE locales_npc_text SET Text0_0_loc9='Over by Crystal Lake there lives a lady named Adele Fielder.  She makes patches for the local guards with the extra leather she gets from the local skinner, Helene.  She would probably be the one to see if you\'re interested in learning the profession.',Text0_1_loc9='MISSING ENGLISH Text0:',Text1_0_loc9='Adele Fielder has made a name for herself as a leatherworker around Elwynn.  You can find her working out of her home up by Crystal Lake.',Text1_1_loc9='MISSING ENGLISH Text1:' WHERE entry=4282;
