UPDATE locales_npc_text SET Text0_0_loc9='I\'m afraid your darkness quotient isn\'t sufficient enough for me to bother talking to you any further.  Move along please.',Text0_1_loc9='I\'m afraid your darkness quotient isn\'t sufficient enough for me to bother talking to you any further.  Move along please.',Text1_0_loc9='MISSING ENGLISH Text1:',Text1_1_loc9='MISSING ENGLISH Text1:' WHERE entry=5723;
