UPDATE locales_npc_text SET Text0_0_loc9='Welcome to the Darkmoon Faire, $C!$B$BMy name is Kerri Hicks and I\'d shake your hand but I\'m afraid I\'d break it, because I\'m the strongest woman alive!',Text0_1_loc9='Welcome to the Darkmoon Faire, $C!$B$BMy name is Kerri Hicks and I\'d shake your hand but I\'m afraid I\'d break it, because I\'m the strongest woman alive!',Text1_0_loc9='MISSING ENGLISH Text1:',Text1_1_loc9='MISSING ENGLISH Text1:' WHERE entry=7383;
