UPDATE locales_npc_text SET Text0_0_loc9='Do you wish to learn the blacksmithing profession or is there something else you wish to tell me?',Text0_1_loc9='Do you wish to learn the blacksmithing profession or is there something else you wish to tell me?',Text1_0_loc9='MISSING ENGLISH Text1:',Text1_1_loc9='MISSING ENGLISH Text1:' WHERE entry=8576;
UPDATE locales_npc_text SET Text0_0_loc9='We are very fortunate to be alive, you and I!',Text0_1_loc9='We are very fortunate to be alive, you and I!',Text1_0_loc9='MISSING ENGLISH Text1:',Text1_1_loc9='MISSING ENGLISH Text1:' WHERE entry=8579;
UPDATE locales_npc_text SET Text0_0_loc9='You don\'t see too many apprentices around these parts, do you?  I\'m beginning to think it\'s because the magisters give us all the dangerous work they\'d rather not do themselves.',Text0_1_loc9='MISSING ENGLISH Text0:',Text1_0_loc9='MISSING ENGLISH Text1:',Text1_1_loc9='MISSING ENGLISH Text1:' WHERE entry=8580;
