UPDATE locales_npc_text SET Text0_0_loc9='You come in search of training, hunter?',Text0_1_loc9='You come in search of training, hunter?',Text1_0_loc9='MISSING ENGLISH Text1:',Text1_1_loc9='MISSING ENGLISH Text1:' WHERE entry=8585;
