UPDATE locales_npc_text SET Text0_0_loc9='This land has been ravaged by the crash. While you may not follow our philosophies, $C, I urge you to do whatever you can to aid in the healing process.',Text0_1_loc9='This land has been ravaged by the crash. While you may not follow our philosophies, $C, I urge you to do whatever you can to aid in the healing process.',Text1_0_loc9='MISSING ENGLISH Text1:',Text1_1_loc9='MISSING ENGLISH Text1:' WHERE entry=9194;
