UPDATE locales_npc_text SET Text0_0_loc9='<Isfar sighs.>$B$BA couple of treehouses don\'t make a home, but it was vast improvement over the Sethekk Halls.$B$BComfortable as it was in Shattrath, I can\'t bring myself to return to my true home in Skettis without my sister. She\'s still trapped in the Sethekk Halls, held prisoner by our older brother.',Text0_1_loc9='MISSING ENGLISH Text0:',Text1_0_loc9='MISSING ENGLISH Text1:',Text1_1_loc9='MISSING ENGLISH Text1:' WHERE entry=9634;
