UPDATE locales_npc_text SET Text0_0_loc9='There are a number of skilled tradesmen scattered around Azuremyst. Which one are you looking for?',Text0_1_loc9='There are a number of skilled tradesmen scattered around Azuremyst. Which one are you looking for?',Text1_0_loc9='MISSING ENGLISH Text1:',Text1_1_loc9='MISSING ENGLISH Text1:' WHERE entry=10087;
UPDATE locales_npc_text SET Text0_0_loc9='Daedal knows his way around the art of brewing potions. He works closely with Fateema in western Azure Watch.',Text0_1_loc9='Daedal knows his way around the art of brewing potions. He works closely with Fateema in western Azure Watch.',Text1_0_loc9='MISSING ENGLISH Text1:',Text1_1_loc9='MISSING ENGLISH Text1:' WHERE entry=10088;
UPDATE locales_npc_text SET Text0_0_loc9='We have no smithy set up in Azure Watch. However, I\'ve seen a few young blacksmiths coming from that odd encampment on the south coast. Perhaps you should investigate.',Text0_1_loc9='We have no smithy set up in Azure Watch. However, I\'ve seen a few young blacksmiths coming from that odd encampment on the south coast. Perhaps you should investigate.',Text1_0_loc9='MISSING ENGLISH Text1:',Text1_1_loc9='MISSING ENGLISH Text1:' WHERE entry=10089;
