UPDATE locales_npc_text SET Text0_0_loc9='The local artificer, Daelo, can teach you the basics of engineering. Perhaps you can help our technician fix that malfunctioning communications device!',Text0_1_loc9='The local artificer, Daelo, can teach you the basics of engineering. Perhaps you can help our technician fix that malfunctioning communications device!',Text1_0_loc9='MISSING ENGLISH Text1:',Text1_1_loc9='MISSING ENGLISH Text1:' WHERE entry=10092;
UPDATE locales_npc_text SET Text0_0_loc9='Fateema is our anchorite, and she has been invaluable in tending crash victims. She practices her trade on the western side of the Watch.',Text0_1_loc9='Fateema is our anchorite, and she has been invaluable in tending crash victims. She practices her trade on the western side of the Watch.',Text1_0_loc9='MISSING ENGLISH Text1:',Text1_1_loc9='MISSING ENGLISH Text1:' WHERE entry=10093;
