UPDATE locales_npc_text SET Text0_0_loc9='As representatives of the Explorers\' League, Bronwyn and I are here at the Alliance\'s behest to study these mountains.$B$BBut between you and me, after an extended stay in Silithus, we just needed a vacation.  This seems like a nice enough spot.',Text0_1_loc9='MISSING ENGLISH Text0:',Text1_0_loc9='MISSING ENGLISH Text1:',Text1_1_loc9='MISSING ENGLISH Text1:' WHERE entry=10270;
