UPDATE locales_npc_text SET Text0_0_loc9='Oh, thank you!  The pit of blackness inside of me has been lifted!  I can see again... and I see the face of my savior!',Text0_1_loc9='MISSING ENGLISH Text0:',Text1_0_loc9='MISSING ENGLISH Text1:',Text1_1_loc9='MISSING ENGLISH Text1:' WHERE entry=10706;
