UPDATE locales_npc_text SET Text2_0_loc9='I like to play in the water barrel but I\'m told it\'s not for children.',Text2_1_loc9='I like to play in the water barrel, but I\'m told it\'s not for children.',Text3_0_loc9='They say the barrel and buckets are for fighting fires.  Are you a fire fighter?',Text3_1_loc9='They say the barrel and buckets are for fighting fires.  Are you a fire fighter?' WHERE entry=11477;
UPDATE locales_npc_text SET Text4_0_loc9='The Headless Horseman lives in the Scarlet Monastery with all the other creepy ghosts and humans.',Text4_1_loc9='The Headless Horseman lives in the Scarlet Monastery with all the other creepy ghosts and humans.',Text5_0_loc9='They\'re giving candies and treats at the inn!  Guardian Grimwatch says if they don\'t give us a treat... then we get to play a trick on them!',Text5_1_loc9='They\'re giving candies and treats at the inn!  Guardian Grimwatch says if they don\'t give us a treat... then we get to play a trick on them!' WHERE entry=11477;
