UPDATE locales_npc_text SET Text0_0_loc9='I understand that many individuals seek romance in taverns and festivals like this one, but alcohol dulls the senses and makes calculating the difficulties of daily life all fuzzy!$B$BBut I\'ve found a solution! Through many trials and tribulations, I\'ve engineered an extraordinary device that gives you all the benefits of alcohol with none of the drawbacks! With these goggles on, ANYONE looks attractive!$B$BMmm... including you, cutie! Tell you what. I\'ll give you a discount!',Text0_1_loc9='I understand that many individuals seek romance in taverns and festivals like this one, but alcohol dulls the senses and makes calculating the difficulties of daily life all fuzzy!$B$BBut I\'ve found a solution! Through many trials and tribulations, I\'ve engineered an extraordinary device that gives you all the benefits of alcohol with none of the drawbacks! With these goggles on, ANYONE looks attractive!$B$BMmm... including you, cutie! Tell you what. I\'ll give you a discount!',Text1_0_loc9='MISSING ENGLISH Text1:',Text1_1_loc9='MISSING ENGLISH Text1:' WHERE entry=12080;
