UPDATE `trans_words`SET `content_loc9`='I did not expect you to return from your attempt, $N.$B$BYou\'ve fulfilled a destiny greater than that of most immortal beings.$B$BBy slaying C\'Thun you\'ve not only saved all of Azeroth, you\'ve also tipped the scales of cosmic events beyond your comprehension.$B$BAccept these robes as a symbol of the glory and burden that will come as a consequence of your actions.$B$BMay their power aid you in the challenges that await you, god-slayer!' WHERE `word_id`=144066; -- locales_quest - offerrewardtext : entry=8633
UPDATE `trans_words`SET `content_loc9`='You\'ve done well, $N. You\'ve proven you\'re no ordinary mortal.$B$BI give you these with the confidence they\'ll aid you in crushing the vile qiraji.' WHERE `word_id`=143879; -- locales_quest - offerrewardtext : entry=8634
UPDATE `trans_words`SET `content_loc9`='The battles of ages past are seeds from which legends grow. Yours is a time of heroes, young one. May your legend take root, thrive.$B$BI bid you well, $N, and offer you this token...' WHERE `word_id`=144070; -- locales_quest - offerrewardtext : entry=8635
UPDATE `trans_words`SET `content_loc9`='The stuff of life is doomed to die and return to the earth, but the stuff of spirit lives eternal. I bid you well, $N, and offer you this token...' WHERE `word_id`=144072; -- locales_quest - offerrewardtext : entry=8636
UPDATE `trans_words`SET `content_loc9`='You\'ve done well, $N. You\'ve proven you\'re no ordinary mortal.$B$BI give you these with the confidence they\'ll aid you in crushing the vile qiraji.' WHERE `word_id`=143879; -- locales_quest - offerrewardtext : entry=8637
