UPDATE locales_npc_text SET Text0_1_loc8='MISSING ENGLISH Text0:', Text1_0_loc8='We could certainly use another cook around Dolanaar, not that Zarrin\'s cooking is lacking mind you.  Why don\'t you go see what he can teach you?',, Text1_1_loc8='MISSING ENGLISH Text1:' WHERE entry=4330;
