UPDATE `locales_npc_text` SET `Text0_1_loc6`='', `Text1_0_loc6`='Go to the Valley of Strength and look for the Bank of Orgrimmar to the south of the Sky Tower.', WHERE `entry`=2554;
UPDATE `locales_npc_text` SET `Text0_1_loc6`='', `Text1_0_loc6`='Where did you think the Wind Rider Master would be?  Head to the top of The Sky Tower in the Valley of Strength!', WHERE `entry`=2555;
UPDATE `locales_npc_text` SET `Text0_1_loc6`='', `Text1_0_loc6`='The Horde Embassy is in the Valley of Strength on the west side.', WHERE `entry`=2556;
UPDATE `locales_npc_text` SET `Text0_1_loc6`='', `Text1_0_loc6`='Look for Innkeeper Grishka in the Valley of Strength. Her inn is on the east side, north of the main city gates.', WHERE `entry`=2557;
UPDATE `locales_npc_text` SET `Text0_1_loc6`='', `Text1_0_loc6`='You can pick up your love letters outside the bank in the middle of the Valley of Strength.', WHERE `entry`=2558;
UPDATE `locales_npc_text` SET `Text0_1_loc6`='', `Text1_0_loc6`='Shoot straight to the northwest section of the Valley of Honor. There you will find the Hunter\'s Hall.', WHERE `entry`=2559;
UPDATE `locales_npc_text` SET `Text0_1_loc6`='', `Text1_0_loc6`='What, you can\'t port there?  Go to the Darkbriar Lodge next to the lake in the Valley of Spirits, up above and to the west of the Valley of Strength.', WHERE `entry`=2560;
UPDATE `locales_npc_text` SET `Text0_1_loc6`='', `Text1_0_loc6`='Healing is for those who cannot endure pain. However if you must, seek out the Spirit Lodge on the lake in the Valley of Spirits, up and above the Valley of Strength to the west.', WHERE `entry`=2561;
UPDATE `locales_npc_text` SET `Text0_1_loc6`='', `Text1_0_loc6`='Your search is almost completed. Seek your truth in the entrance room of Thrall\'s Fortress in the Valley of Wisdom.', WHERE `entry`=2562;
