UPDATE `locales_npc_text` SET `Text0_1_loc6`='', `Text1_0_loc6`='Oh, the tram, the tram... everyone wants to ride the tram. What happened to the good ol\' days of flying on the back of a gryphon, gripping the saddle for dear life as you flew up high in the sky above?  $B$BBah!  You go take the tram then... it\'s over in Tinker Town with the rest of the gnomish contraptions.', WHERE `entry`=3814;

