UPDATE `locales_npc_text` SET `Text0_1_loc6`='', `Text1_0_loc6`='Tai\'jin is her name and you\'ll find her inside the barracks on the east side of Razor Hill. She\'ll teach you to respect the gods. Make certain that you show her the proper respect in return!', WHERE `entry`=4015;
