UPDATE `locales_npc_text` SET `Text0_1_loc6`='', `Text1_0_loc6`='Lee\'s caught some pretty odd things up at Crystal Lake. He told me a story about this weird mask he fished up some months ago. If you ask me, I think he just had one too many at the Lion\'s Pride that night.', WHERE `entry`=4280;
