UPDATE `locales_npc_text` SET `Text0_1_loc6`='', `Text1_0_loc6`='If ye ask me, I think Grif spends a little too much time with the animals. Still, nobody else knows the hunter\'s path quite like he does. Look for him outside the smithy in Kharanos.', WHERE `entry`=4293;
UPDATE `locales_npc_text` SET `Text0_1_loc6`='', `Text1_0_loc6`='Ye sure ye don\'t want to learn to be a warrior?  Well, it\'s yer hide on the battlefield. If ye really want to learn to waggle yer finger, ye best talk to Magis Sparkmantle in the Thunderbrew Distillery.', WHERE `entry`=4294;
