UPDATE `locales_npc_text` SET `Text0_1_loc6`='', `Text1_0_loc6`='Only a few spots to get any fishing done around here. Paxton knows them all and probably a few he won\'t tell ya about. Why don\'t ye go over to Iceflow Lake and see what he will tell ya.', WHERE `entry`=4307;
